const showMenu=(toggleId,navId)=>{const toggle=document.getElementById(toggleId),nav=document.getElementById(navId);toggle&&nav&&toggle.addEventListener("click",()=>{nav.classList.toggle("show"),toggle.classList.toggle("bx-x")})};showMenu("header-toggle","nav-menu");const navLink=document.querySelectorAll(".nav__link");function linkAction(){navLink.forEach(n=>n.classList.remove("active")),this.classList.add("active")}navLink.forEach(n=>n.addEventListener("click",linkAction));const btnMenu=document.querySelector(".header__logo"),menu=document.querySelector(".nav__name");btnMenu.addEventListener("click",()=>{menu.classList.toggle("active")});const allLinks=document.querySelectorAll(".item-nav");function scrollUp(){const scrollUp=document.getElementById("scroll-up");this.scrollY>=300?scrollUp.classList.add("show-scroll"):scrollUp.classList.remove("show-scroll")}allLinks.forEach(item=>{item.addEventListener("click",()=>{menu.classList.remove("active")})}),window.addEventListener("scroll",scrollUp);